import React, { useState } from 'react';

import { Card, Avatar, Box, CardActions, CardContent, CardMedia, Button, Typography } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import DeleteIcon from '@mui/icons-material/Delete';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import moment from 'moment';

const Post = ({ post, setCurrentId, name, profilePicture }) => {
  const [liked, setLiked] = useState(false);
  const [disable, setDisable] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('token'));
  async function likePost(_id, likeCount) {
    setDisable(true);
    if (liked) likeCount = likeCount - 1
    else likeCount = likeCount + 1
    const req = await fetch("http://localhost:1337/api/likePost", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        _id,
        token,
        likeCount,
      })
    });
    const data = await req.json();
    console.log(data);
    setLiked(!liked);
    setDisable(false);
  }

  async function deletePost(_id) {

    const req = await fetch("http://localhost:1337/api/deletePost", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        _id
      })
    });
    const data = await req.json();
    console.log(data);
  }

  function goToProfile() {

    localStorage.setItem('id', post._id)

    // localStorage.setItem('_id',post._id)
    window.location.href = "/profile";
  }
  return (
    post ?
      <Card className="card" sx={{ borderRadius: 5 }}>
        <Box className='centered' sx={{mt:10}}>

          <Box className='centered' sx={{justifyContent:'center'}}>
            <Avatar src={profilePicture}>{name[0]}</Avatar>
            <Box>
              <Button size="small" color="primary" onClick={() => goToProfile()}><Typography variant="h6" sx={{ color: 'gray' }} >{name}</Typography></Button>
              <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
            </Box>
          </Box>
          <Button style={{ color: 'gray' }} size="small" onClick={() => setCurrentId(post._id)}><MoreHorizIcon fontSize="medium" /></Button>

        </Box>
        {/* <CardMedia sx={{ height:1400,display:'flex' }} image={post.selectedFile} title={post.title} /> */}
        <img src={post.selectedFile} style={{ width: '100%', height: '100%' }} alt="signIN" />
        {/* <Image style={{ height:1400,width:500,display:'flex' }} src={post.selectedFile}></Image> */}

        <div className='details'>
          <Typography variant="body2" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
        </div>
        <Typography className='title' gutterBottom variant="h5" component="h2">{post.title}</Typography>
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="p">{post.message}</Typography>
        </CardContent>
        <CardActions className='cardActions'>
          <Button size="small" color="primary" disabled={disable ? true : false} onClick={() => likePost(post._id, post.likeCount)}><ThumbUpIcon color={liked ? 'primary' : 'disabled'} fontSize="small" /> Like {post.likeCount} </Button>
          <Button size="small" color="primary" onClick={() => deletePost(post._id)}><DeleteIcon fontSize="small" /> Delete</Button>
        </CardActions>
      </Card>
      : <></>
  );
};

export default Post;
