import { Grid, CircularProgress, Link, Avatar,Divider, ListItemButton, Button, Box, Typography } from '@mui/material';
import React, { useEffect, useState } from "react";
import FriendRequests from '../FriendRequests';

function People() {
  const [allProfiles, setAllProfiles] = useState();
  const [sendertoken, setSendertoken] = useState(localStorage.getItem('token'));
  const [requestStatus, setRequestStatus] = useState([]);
  const [friendStatus, setFriendStatus] = useState('Add Friend');
  const [requesterEmail, setRequesterEmail] = useState('');
  const [processing, setProcessing] = useState(false);
  async function getProfiles() {
    const req = await fetch("http://localhost:1337/api/getAllProfiles", {
      headers: {
        'x-access-token': localStorage.getItem('token')
      },
    });
    const data = await req.json();
    setAllProfiles(data.profiles);
    setFriendStatus(data.friendStatus);
    setRequesterEmail(data.requesterEmail);
  }

  async function addFriend(recevierEmail) {
    setProcessing(true);
    const req = await fetch("http://localhost:1337/api/addFriend", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        recevierEmail,
        sendertoken,
      })
    });
    const data = await req.json();
    setProcessing(false);
  }

  async function cancelRequest(recevierEmail) {
    setProcessing(true);
    const req = await fetch("http://localhost:1337/api/cancelRequest", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        recevierEmail,
        sendertoken,
      })
    });
    const data = await req.json();
    setProcessing(false);
  }

  async function confirmRequest(recevierEmail) {
    setProcessing(true);
    const req = await fetch("http://localhost:1337/api/confirmRequest", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        recevierEmail,
        sendertoken,
      })
    });
    const data = await req.json();
    setProcessing(false);
  }

  async function deleteRequest(recevierEmail) {
    setProcessing(true);
    const req = await fetch("http://localhost:1337/api/deleteRequest", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        recevierEmail,
        sendertoken,
      })
    });
    const data = await req.json();
    setProcessing(false);
  }


  async function removeFriend(recevierEmail) {
    setProcessing(true);
    const req = await fetch("http://localhost:1337/api/removeFriend", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        recevierEmail,
        sendertoken,
      })
    });
    const data = await req.json();
    setProcessing(false);
  }


  useEffect(() => {
    getProfiles();
  });
  return (

    allProfiles ?
      !allProfiles.length ? <Box className='circularProgress'> <CircularProgress /></Box> : (
        <Box sx={{width:'100%'}}>
              <Typography align="center" color='gray' variant="h5">Friend Requests</Typography> 
              <Divider/>
              <FriendRequests caller={'people'}/>
              <Typography align="center" color='gray' sx={{my:2}} variant="h5">People you may know</Typography> 
              <Divider/>
              {/* <Typography align="center" variant="h5">People you may know</Typography> */}
          <Grid className='container' sx={{ alignItems: "center", flexDirection: 'column', justifyContent: 'center' }} container spacing={2}>

            {allProfiles.map((profile, index) => (

              //  index=index+1,
              //  console.log(friendStatus),
              <Grid sx={{ width: '100%' }} key={profile._id} item xs={12} sm={12} >

                <Box className='friendRequests'>
                  <Box sx={{ p: 1, m: 0, borderRadius: 3, flexDirection: 'column', alignItems: 'center' }} >
                    <Box sx={{ flexDirection: 'row', display: 'flex', width: '100%', justifyContent: 'space-between' }}>

                      <Avatar src={profile.profilePicture} sx={{ width: 60, height: 60 }}></Avatar>
                      <Box sx={{ width: '100%', ml: '3%', }}>
                        <Typography sx={{ color: 'lack', fontWeight: 900, mt: '3%' }}>{profile.firstName + ' ' + profile.lastName}</Typography>
                        <Typography sx={{ color: 'gray', fontSize: '13px', fontWeight: 200, mt: '3%' }}>0 mutual friends</Typography>
                      </Box>

                      {/* {console.log("=================Start=================")}
                        {console.log("profile.friendRequests[index]: ", profile.friendRequests[index])}
                        {console.log("requesterEmail ",requesterEmail)}
                        {console.log("profile.friendRequests[index] != requesterEmail ", profile.friendRequests[index] != requesterEmail)}
                        {console.log("==================================")}
                    */}

                      {profile.friends[index] != requesterEmail ?

                        (profile.friendRequested[index] != requesterEmail ?
                          <Box sx={{width:'100%',display:'flex',justifyContent:'flex-end'}}>
                            <Button variant="contained" className='friendsButton1' onClick={() => addFriend(profile.email)}      sx={{ disable: processing ? 'true' : 'false', display: profile.friendRequests[index] == requesterEmail ? 'none' : 'flex' }}>Add Friend</Button>
                            <Button variant="contained" className='friendsButton1' onClick={() => cancelRequest(profile.email)} sx={{ disable: processing ? 'true' : 'false', display: profile.friendRequests[index] != requesterEmail ? 'none' : 'flex',backgroundColor: 'gray' }}>Cancel ---></Button>
                          </Box>
                          : 
                          <Box sx={{width:'100%',display:'flex',justifyContent:'flex-end',flexDirection:'row'}}>
                          <Button variant="contained" className='friendsButton'  onClick={() => confirmRequest(profile.email)} sx={{ disable: processing ? 'true' : 'false' }}>Confirm</Button>
                          <Button variant="contained" className='friendsButton'  onClick={() => deleteRequest(profile.email)} sx={{ disable: processing ? 'true' : 'false', ml:5, backgroundColor: 'gray' }}>Delete</Button>
                          </Box>
                        )
                        : 
                        <Box sx={{width:'100%',display:'flex',justifyContent:'flex-end',flexDirection:'row'}}>
                        <Button variant="contained" className='friendsButton' sx={{ disable: processing ? 'true' : 'false', ml:5, backgroundColor: 'aaa' }}> Friends </Button>
                        <Button variant="contained" className='friendsButton'   onClick={() => removeFriend(profile.email)}>Remove</Button>
                        </Box>
                      }
                    </Box>
                  </Box>
                </Box>

              </Grid>
            ))}
          </Grid>
        </Box>
      )
      : <></>
  );
}

export default People
